create table db_table_field
(
    id                bigint auto_increment comment '主键ID'
        primary key,
    table_id          bigint                             not null comment '所属表ID（关联db_table.id）',
    field_name        varchar(128)                       not null comment '字段名',
    field_type        varchar(50)                        not null comment '字段类型（VARCHAR, INT, DATETIME等）',
    field_length      int                                null comment '字段长度',
    decimal_digits    int                                null comment '小数位数',
    is_nullable       tinyint  default 1                 not null comment '是否可空：1-可空，0-不可空',
    column_default    varchar(500)                       null comment '默认值',
    is_primary_key    tinyint  default 0                 not null comment '是否主键：1-是，0-否',
    is_auto_increment tinyint  default 0                 not null comment '是否自增：1-是，0-否',
    field_comment     varchar(500)                       null comment '字段注释',
    field_label       varchar(128)                       null comment '字段显示标签（用户可编辑）',
    ordinal_position  int                                not null comment '字段在表中的位置（从1开始）',
    create_time       datetime default CURRENT_TIMESTAMP not null comment '创建时间',
    update_time       datetime default CURRENT_TIMESTAMP not null on update CURRENT_TIMESTAMP comment '更新时间',
    deleted           tinyint  default 0                 not null comment '逻辑删除：1-删除，0-存在',
    constraint uk_table_field
        unique (table_id, field_name, deleted)
)
    comment '字段管理表';

create index idx_field_name
    on db_table_field (field_name);

create index idx_ordinal_position
    on db_table_field (ordinal_position);

create index idx_table_id
    on db_table_field (table_id);

